import { Col, Form } from "react-bootstrap";

export function RequestForm2ndRow({
  contribution,
  contributionPercent,
  handleContributionField,
  setContribution,
  setContributionPercent,
  priceInputValue,
}) {
  const handleContributionInputChanges = (e) => {
    setContribution(e.target.value);
    setContributionPercent(priceInputValue / e.target.value);
    handleContributionField();
  };

  const handleContributionPercentChanges = (e) => {
    setContribution((priceInputValue * e.target.value) / 100);
    setContributionPercent(e.target.value);
    handleContributionField();
  };

  return (
    <>
      <Col md={6}>
        <Form.Group>
          <Form.Label>Pradinis įnašas</Form.Label>
          <Form.Control
            type="number"
            placeholder="0"
            value={contribution}
            onBlur={(e) => handleContributionInputChanges(e)}
          />
          <Form.Text>10% automobilio sumos</Form.Text>
        </Form.Group>
      </Col>
      <Col md={6}>
        <Form.Group>
          <Form.Label>Pradinis įnašas %</Form.Label>
          <Form.Control
            type="number"
            placeholder="0"
            value={contributionPercent}
            onBlur={(e) => handleContributionPercentChanges(e)}
          />
        </Form.Group>
      </Col>
    </>
  );
}
